import collections
import time
import os
import sys
import bisect
import heapq
from typing import List


class Solution:
    def splitArray(self, nums: List[int]) -> bool:
        N = len(nums)
        if N < 3:
            return False
        
        q = [nums[0]]
        for i in range(1, N):
            if nums[i] == 0 and nums[i-1] == 0:
                continue
            q.append(nums[i])

        nums = q
        N = len(nums)
        presum = [0] * (N + 1)
        pi = collections.defaultdict(list)
        for i, v in enumerate(nums):
            presum[i + 1] = presum[i] + v
            pi[presum[i + 1]].append(i + 1)
        
        for i in range(1, N):
            s = presum[i]
            
            t = presum[i + 1] + s
            ti = pi[t]
            tii = bisect.bisect_right(ti, i)
            if tii < 0 or tii >= len(ti):
                continue
            
            for j in ti[tii:]:
                if j + 1 >= N:
                    break
                
                u = presum[j + 1] + s
                ui = pi[u]
                uii = bisect.bisect_right(ui, j)
                if uii < 0 or uii >= len(ui):
                    continue
                
                for k in ui[uii:]:
                    if k + 1 >= N:
                        continue
                    if presum[-1] - presum[k + 1] == s:
                        return True
        
        return False
        
        

